import React, { useRef, useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';

function App() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [stream, setStream] = useState(null);
  const [isStreaming, setIsStreaming] = useState(false);
  const [error, setError] = useState(null);
  const [serverStatus, setServerStatus] = useState('checking');
  const [capturedImage, setCapturedImage] = useState(null);

  // Check server connection on mount
  useEffect(() => {
    checkServerConnection();
    const interval = setInterval(checkServerConnection, 5000);
    return () => clearInterval(interval);
  }, []);

  // Update API URL based on current hostname (for phone access)
  useEffect(() => {
    if (window.location.hostname !== 'localhost' && window.location.hostname !== '127.0.0.1') {
      const currentHost = window.location.hostname;
      // Update API URL to use the same hostname
      const newApiUrl = `http://${currentHost}:3001`;
      // Note: This won't update the const, but we can use it in the component
      console.log('Detected external access, API should be:', newApiUrl);
    }
  }, []);

  const checkServerConnection = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/health`);
      setServerStatus('connected');
    } catch (err) {
      setServerStatus('disconnected');
    }
  };

  const startCamera = async () => {
    try {
      setError(null);

      // Check if mediaDevices is available
      if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
        // Fallback for older browsers
        const getUserMedia = navigator.mediaDevices?.getUserMedia ||
                            navigator.getUserMedia ||
                            navigator.webkitGetUserMedia ||
                            navigator.mozGetUserMedia ||
                            navigator.msGetUserMedia;

        if (!getUserMedia) {
          throw new Error('Camera API not supported in this browser. Please use Safari on iOS or a modern browser.');
        }

        // Use fallback API (older style, returns a promise via callback)
        const stream = await new Promise((resolve, reject) => {
          getUserMedia.call(
            navigator.mediaDevices || navigator,
            { video: { facingMode: 'environment' } },
            resolve,
            reject
          );
        });

        if (videoRef.current) {
          videoRef.current.srcObject = stream;
          setStream(stream);
          setIsStreaming(true);
        }
        return;
      }

      // Modern API
      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: {
          facingMode: 'environment', // Use back camera on mobile
          width: { ideal: 1280 },
          height: { ideal: 720 }
        }
      });

      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
        setStream(mediaStream);
        setIsStreaming(true);
      }
    } catch (err) {
      const errorMessage = err.message || err.name || 'Unknown error';
      setError(`Camera access denied: ${errorMessage}. Make sure you're using Safari on iOS and have granted camera permissions.`);
      console.error('Camera error:', err);
    }
  };

  const stopCamera = () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
      setIsStreaming(false);
      if (videoRef.current) {
        videoRef.current.srcObject = null;
      }
    }
  };

  const capturePhoto = () => {
    if (videoRef.current && canvasRef.current) {
      const video = videoRef.current;
      const canvas = canvasRef.current;
      const context = canvas.getContext('2d');

      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      context.drawImage(video, 0, 0);

      const imageData = canvas.toDataURL('image/jpeg', 0.8);
      setCapturedImage(imageData);
      sendToServer(imageData);
    }
  };

  const sendToServer = async (imageData) => {
    try {
      await axios.post(`${API_URL}/api/camera-data`, {
        image: imageData,
        timestamp: new Date().toISOString(),
        device: navigator.userAgent
      });
      console.log('Image sent to server successfully');
    } catch (err) {
      console.error('Error sending to server:', err);
      setError(`Failed to send to server: ${err.message}`);
    }
  };

  const startAutoCapture = () => {
    if (!isStreaming) {
      startCamera();
      setTimeout(() => {
        const interval = setInterval(() => {
          if (isStreaming) {
            capturePhoto();
          } else {
            clearInterval(interval);
          }
        }, 2000); // Capture every 2 seconds
      }, 1000);
    }
  };

  return (
    <div className="App">
      <div className="container">
        <h1>üì± Phone Camera App</h1>

        <div className="status-indicator">
          <div className={`status-dot ${serverStatus}`}></div>
          <span>
            Server: {serverStatus === 'connected' ? 'Connected' : 
                    serverStatus === 'checking' ? 'Checking...' : 'Disconnected'}
          </span>
        </div>

        {error && <div className="error-message">{error}</div>}

        <div className="video-container">
          <video
            ref={videoRef}
            autoPlay
            playsInline
            muted
            className="video-preview"
          />
          <canvas ref={canvasRef} style={{ display: 'none' }} />
        </div>

        {capturedImage && (
          <div className="captured-image-container">
            <h3>Last Captured Image:</h3>
            <img src={capturedImage} alt="Captured" className="captured-image" />
          </div>
        )}

        <div className="controls">
          {!isStreaming ? (
            <>
              <button onClick={startCamera} className="btn btn-primary">
                üì∑ Start Camera
              </button>
              <button onClick={startAutoCapture} className="btn btn-secondary">
                üîÑ Auto Capture Mode
              </button>
            </>
          ) : (
            <>
              <button onClick={capturePhoto} className="btn btn-primary">
                üì∏ Capture Photo
              </button>
              <button onClick={stopCamera} className="btn btn-danger">
                ‚èπ Stop Camera
              </button>
            </>
          )}
        </div>

        <div className="info">
          <p>üìù Instructions:</p>
          <ol>
            <li>Make sure the server is running on your laptop</li>
            <li>Allow camera access when prompted</li>
            <li>Use "Capture Photo" to send images to your laptop</li>
            <li>Or use "Auto Capture Mode" for continuous capture</li>
          </ol>
        </div>
      </div>
    </div>
  );
}

export default App;
